import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

const likertOptions = [
  'Not at all',
  'Rarely',
  'Sometimes',
  'Often',
  'Nearly every day'
];

const stressQuestions = [
  'Do you feel that important things in your life are out of your control?',
  'Do you often feel overwhelmed by everything happening at once?',
  'Do small issues cause you a lot of emotional strain?',
  'Do you often feel exhausted even when you havenâ€™t done much physically?',
  'Do you get irritable or short-tempered easily?'
];

function App() {
  const [step, setStep] = useState('welcome');
  const [questionIdx, setQuestionIdx] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const startStressCheck = () => {
    setStep('stress');
    setQuestionIdx(0);
    setAnswers([]);
    setResponse('');
  };

  const handleAnswer = async (idx) => {
    const newAnswers = [...answers, idx];
    if (questionIdx < stressQuestions.length - 1) {
      setAnswers(newAnswers);
      setQuestionIdx(questionIdx + 1);
    } else {
      // Submit to backend
      setLoading(true);
      try {
        const res = await fetch('http://127.0.0.1:5000/api/stress-check', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ answers: newAnswers })
        });
        const data = await res.json();
        setResponse(data.result);
      } catch (err) {
        setResponse('Error connecting to backend.');
      }
      setLoading(false);
      setStep('stress-result');
      setAnswers([]);
      setQuestionIdx(0);
    }
  };

  const handleContinue = () => {
    setStep('main-menu');
    setResponse('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h2>MindCare Demo</h2>
        {step === 'welcome' && (
          <>
            <p>Hi, Iâ€™m MindCare ðŸŒ±<br/>Iâ€™m here to help you check in with yourself gently and privately.<br/>How are you feeling today?</p>
            <button onClick={() => setStep('main-menu')}>Start</button>
          </>
        )}
        {step === 'main-menu' && (
          <>
            <p>What would you like to explore?</p>
            <button onClick={startStressCheck}>ðŸ§  Stress Check</button>
            {/* Add more feature buttons here */}
          </>
        )}
        {step === 'stress' && (
          <>
            <p>{stressQuestions[questionIdx]}</p>
            {likertOptions.map((opt, idx) => (
              <button key={opt} onClick={() => handleAnswer(idx)} disabled={loading}>{opt}</button>
            ))}
          </>
        )}
        {step === 'stress-result' && (
          <>
            <p>{response}</p>
            <button onClick={handleContinue}>Back to Main Menu</button>
          </>
        )}
      </header>
    </div>
  );
}

export default App;
