import React, { useState } from 'react';
import './SadOMeterPage.css'; // Reuse styles for consistency
import ScoreMeter from './ScoreMeter';
import GaugeScoreMeter from './GaugeScoreMeter';
import './GaugeScoreMeter.css';
import Header from './Header';

const questions = [
  {
    text: 'How often do you have trouble falling or staying asleep?',
    type: 'scale',
    options: ['Never', 'Rarely', 'Sometimes', 'Often', 'Very Often']
  },
  {
    text: 'Do you wake up feeling rested?',
    type: 'mcq',
    options: ['Always', 'Sometimes', 'Rarely', 'Never']
  },
  {
    text: 'Do you use your phone or scroll social media in bed?',
    type: 'mcq',
    options: ['Yes', 'No', 'Sometimes']
  },
  {
    text: 'How many hours do you usually sleep per night?',
    type: 'mcq',
    options: ['Less than 4', '4â€“6', '6â€“8', 'More than 8']
  },
  {
    text: 'Do you have disturbing dreams or nightmares?',
    type: 'scale',
    options: ['Never', 'Rarely', 'Sometimes', 'Often', 'Very Often']
  },
  {
    text: 'Do you feel tired during the day despite sleeping?',
    type: 'scale',
    options: ['Never', 'Rarely', 'Sometimes', 'Often', 'Very Often']
  }
];

const emojiReactions = ['ðŸ˜´', 'ðŸ¥±', 'ðŸ˜•', 'ðŸ˜«', 'ðŸ˜µ'];

export default function SleepStruggleScannerPage() {
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [showMindBot, setShowMindBot] = useState(false);

  const handleAnswer = (val) => {
    setAnswers(a => [...a, val]);
    setStep(s => s + 1);
  };

  const handleSubmit = async () => {
    setLoading(true);
    setResult('');
    setScore(null);
    try {
      const res = await fetch('/api/mental-health-predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          questions: questions.map(q => q.text),
          answers
        })
      });
      const data = await res.json();
      const match = data.result.match(/score is (\d{1,3})/i);
      let scoreVal = match ? parseInt(match[1]) : null;
      if (scoreVal === null || isNaN(scoreVal)) scoreVal = 60;
      setScore(scoreVal);
      setResult(data.result);
    } catch (e) {
      setResult('Error: ' + e.message);
    }
    setLoading(false);
  };

  // Animated emoji based on step
  const emoji = emojiReactions[Math.min(Math.floor((step / questions.length) * emojiReactions.length), emojiReactions.length - 1)];

  return (
    <div className="sadometer-root">
      <Header onMindBotClick={() => setShowMindBot(true)} />
      <div style={{display:'flex', flexDirection:'column', alignItems:'center'}}>
        <h2 style={{marginBottom: 0}}>Sleep Struggle Scanner <span style={{fontSize: 28}}>{emoji}</span></h2>
        <div className="sadometer-progress-bar">
          <div className="sadometer-progress" style={{width: `${(step / questions.length) * 100}%`}} />
        </div>
        {score === null && step < questions.length && (
          <div className="sadometer-question-card">
            <div className="sadometer-question">{questions[step].text}</div>
            <div className="sadometer-options">
              {questions[step].options.map((opt, i) => (
                <button
                  key={i}
                  className="sadometer-option-btn"
                  onClick={() => handleAnswer(opt)}
                  disabled={loading}
                >
                  {opt}
                </button>
              ))}
            </div>
          </div>
        )}
        {step === questions.length && score === null && (
          <div style={{marginTop: 24}}>
            <button className="feature-btn" onClick={handleSubmit} disabled={loading}>
              {loading ? 'Analyzing...' : 'See My Sleep Score'}
            </button>
          </div>
        )}
        {score !== null && (
          <div style={{marginTop: 32}}>
            <GaugeScoreMeter
              score={score}
              label="Your Sleep Score"
              subLabel="Sleep Wellness"
              tooltip="Higher is better! This is your sleep wellness score."
              goodIsHigh={true}
            />
            {result && <div style={{marginTop: 18, fontWeight: 600, color: '#4caf50'}}>{result}</div>}
          </div>
        )}
      </div>
      {showMindBot && <div style={{zIndex: 4000, position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh'}}><Header onMindBotClick={() => setShowMindBot(false)} /></div>}
    </div>
  );
}
