import React, { useState, useMemo } from 'react';
import GaugeScoreMeter from './GaugeScoreMeter';
import './GaugeScoreMeter.css';
import './SadOMeterPage.css';
import Header from './Header';

const SADOMETER_QUESTION_POOL = [
  'How often do you feel sad or empty without a clear reason?',
  'Do you find it difficult to enjoy activities you used to love?',
  'How often do you feel hopeless about the future?',
  'Do you struggle to motivate yourself to start daily tasks (work, school, chores)?',
  'How frequently do you feel tired or have little energy, even after rest?',
  'Do you find it hard to focus on things like reading or watching shows?',
  'How often do you feel worthless or like youâ€™re failing others?',
  'Do you have trouble falling asleep, staying asleep, or waking up too early?',
  'Do you sleep much more than usual to avoid facing the day?',
  'How often do you feel restless, agitated, or unable to sit still?',
  'Do you feel like your body is moving or thinking slower than normal?',
  'How often do you find yourself withdrawing from friends and family?',
  'Do you avoid messages, calls, or social invitations even when you know theyâ€™d be good for you?',
  'When youâ€™re on social media, do you compare yourself to others and feel worse about your own life?',
  'Do you find yourself scrolling social media just to distract from your thoughts?',
  'After using social media, do you usually feel more down than before?',
  'Do you find it hard to believe positive things others say about you?',
  'How often do you have thoughts like â€œpeople would be better off without meâ€?',
  'Do you find yourself thinking life is not worth the effort?',
  'How frequently do you cry or feel like crying, even over small things?',
  'Do you blame yourself for things that are out of your control?',
  'Have you lost interest in caring for your appearance or hygiene?',
  'How often do you feel disconnected from your surroundings or like youâ€™re just going through the motions?',
  'Do you struggle to feel close to others, even people you care about?',
  'Do you eat much less or much more than usual without really noticing?',
  'How often do you feel numb, like you canâ€™t feel much of anything at all?',
  'Do you feel guilty for taking breaks or resting, even when youâ€™re exhausted?',
  'Do you feel youâ€™re a burden on those around you?',
  'How often do you feel tense or on edge, even without clear reasons?',
  'Do you find it hard to imagine things improving in your life?'
];

const SADOMETER_OPTIONS = [
  'Never',
  'Rarely',
  'Sometimes',
  'Often',
  'Almost Always'
];

function shuffle(array) {
  const arr = array.slice();
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
}

const emojiReactions = ['ðŸ˜', 'ðŸ˜¢', 'ðŸ˜ž', 'ðŸ˜­', 'ðŸ˜”'];

export default function SadOMeterPage() {
  // Randomly select 20â€“25 questions for this session
  const questions = useMemo(() => shuffle(SADOMETER_QUESTION_POOL).slice(0, Math.floor(20 + Math.random() * 6)), []);
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [showMindBot, setShowMindBot] = useState(false);

  // Randomize options per question
  const optionsForStep = useMemo(() => shuffle(SADOMETER_OPTIONS), [step]);

  const handleAnswer = (val) => {
    setAnswers(a => {
      const newA = [...a];
      newA[step] = val;
      return newA;
    });
    setStep(s => s + 1);
  };

  const handleSubmit = async () => {
    if (answers.length < questions.length || answers.some(a => !a)) {
      setResult('Please answer all questions before submitting.');
      return;
    }
    setLoading(true);
    setResult('');
    setScore(null);
    try {
      const res = await fetch('/api/mental-health-predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          questions,
          answers
        })
      });
      if (!res.ok) {
        setResult('Error: Invalid or incomplete answers. Please answer all questions.');
        setLoading(false);
        return;
      }
      const data = await res.json();
      const match = data.result.match(/score is (\d{1,3})/i);
      let scoreVal = match ? parseInt(match[1]) : null;
      if (scoreVal === null || isNaN(scoreVal)) scoreVal = 60;
      setScore(scoreVal);
      setResult(data.result);
    } catch (e) {
      setResult('Error: ' + e.message);
    }
    setLoading(false);
  };

  // Animated emoji based on step
  const emoji = emojiReactions[Math.min(Math.floor((step / questions.length) * emojiReactions.length), emojiReactions.length - 1)];

  return (
    <div className="sadometer-root">
      <Header onMindBotClick={() => setShowMindBot(true)} />
      <div style={{display:'flex', flexDirection:'column', alignItems:'center'}}>
        <h2 style={{marginBottom: 0}}>Sad-o-Meter <span style={{fontSize: 28}}>{emoji}</span></h2>
        <div className="sadometer-progress-bar">
          <div className="sadometer-progress" style={{width: `${(step / questions.length) * 100}%`}} />
        </div>
        {/* Render all questions at once like Google Forms */}
        {score === null && (
          <form style={{width:'100%', maxWidth:420}} onSubmit={e => {e.preventDefault(); handleSubmit();}}>
            {questions.map((q, idx) => (
              <div key={idx} className="sadometer-question-card" style={{display:'flex', flexDirection:'column', alignItems:'center', marginBottom:18}}>
                <div className="sadometer-question" style={{justifyContent:'center', textAlign:'center'}}>{q}</div>
                <div className="sadometer-options" style={{display:'flex', flexDirection:'column', alignItems:'center'}}>
                  {SADOMETER_OPTIONS.map((opt, i) => (
                    <label key={i} style={{margin:'6px auto', minWidth:120, cursor:'pointer'}}>
                      <input
                        type="radio"
                        name={`q${idx}`}
                        value={opt}
                        checked={answers[idx] === opt}
                        onChange={() => setAnswers(a => { const na = [...a]; na[idx] = opt; return na; })}
                        style={{marginRight:8}}
                        required={true}
                      />
                      {opt}
                    </label>
                  ))}
                </div>
              </div>
            ))}
            <button className="feature-btn" type="submit" style={{marginTop:24}} disabled={loading}>
              {loading ? 'Analyzing...' : 'Get Me My Score'}
            </button>
          </form>
        )}
        {score !== null && (
          <div style={{marginTop: 32}}>
            <GaugeScoreMeter
              score={score}
              label="Your Sadness Score"
              subLabel="Sadness Level"
              tooltip="Lower is better! This is your sadness score."
              goodIsHigh={false}
            />
            {result && <div style={{marginTop: 18, fontWeight: 600, color: '#4caf50'}}>{result}</div>}
          </div>
        )}
        {result && !loading && score === null && (
          <div style={{marginTop: 18, fontWeight: 600, color: 'red'}}>{result}</div>
        )}
      </div>
      {showMindBot && <div style={{zIndex: 4000, position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh'}}><Header onMindBotClick={() => setShowMindBot(false)} /></div>}
    </div>
  );
}
