import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import { Line } from 'react-chartjs-2';

const likertOptions = [
  'Not at all',
  'Rarely',
  'Sometimes',
  'Often',
  'Nearly every day'
];

const riskOptions = [
  'Never',
  'Once or Twice',
  'Sometimes',
  'Often',
  'Prefer not to say'
];

const stressQuestions = [
  'Do you feel that important things in your life are out of your control?',
  'Do you often feel overwhelmed by everything happening at once?',
  'Do small issues cause you a lot of emotional strain?',
  'Do you often feel exhausted even when you haven’t done much physically?',
  'Do you get irritable or short-tempered easily?'
];

const burnoutQuestions = [
  'Do you feel emotionally exhausted at the start of the day?',
  'Do you feel your tasks or goals no longer have meaning for you?',
  'Have you stopped feeling proud or accomplished about your work or studies?',
  'Do you feel distant or numb toward people around you?',
  'Do you often feel like you\'re just “getting through” each day?',
  'Do you no longer have energy to care about the outcome of what you do?'
];

const sleepQuestions = [
  'How often do you struggle to fall asleep at night?',
  'Do you wake up often during the night and stay awake?',
  'Do you feel tired even after a full night’s sleep?',
  'Do you sleep more or less than your usual amount?',
  'Has your sleep made it harder to focus or stay active during the day?'
];

const anxietyQuestions = [
  'Do you often feel nervous, anxious, or on edge?',
  'Do you feel that something terrible might happen unexpectedly?',
  'Does your heart race or breathing become shallow without physical effort?',
  'Do you avoid places or situations because of anxiety?',
  'Do small problems feel overwhelming or frightening?'
];

const moodQuestions = [
  'Do you feel persistently sad, low, or empty?',
  'Have you lost interest in activities you usually enjoy?',
  'Do you feel guilt or worthlessness without clear cause?',
  'Do you find it hard to concentrate or finish tasks?',
  'Do you feel tired or heavy even doing simple things?',
  'Do you feel emotionally disconnected from others?'
];

const riskQuestions = [
  'Have you had thoughts of harming yourself?',
  'Have you felt like life is not worth living?',
  'Have you thought about a plan or imagined ending your life?'
];

const copingQuestions = [
  'Do you feel like you have healthy ways to manage difficult feelings?',
  'Do you have someone you can trust and talk to when you\'re struggling?',
  'Are you able to feel joy or comfort, even briefly, during tough times?',
  'Do you know what activities or routines help you feel better?'
];

function ChatBubble({ text, sender }) {
  return <div className={`bubble ${sender}`}>{text}</div>;
}

function ButtonRow({ options, onClick, disabled }) {
  return (
    <div className="button-row">
      {options.map((opt, idx) => (
        <button key={opt} onClick={() => onClick(idx)} disabled={disabled}>{opt}</button>
      ))}
    </div>
  );
}

function App() {
  const [step, setStep] = useState('welcome');
  const [questionIdx, setQuestionIdx] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);
  const [feature, setFeature] = useState('');
  const [showMindfulness, setShowMindfulness] = useState(false);
  const [showSupport, setShowSupport] = useState(false);
  const [showMoodTracker, setShowMoodTracker] = useState(false);
  const [showResources, setShowResources] = useState(false);
  const [showJournal, setShowJournal] = useState(false);
  const [moodHistory, setMoodHistory] = useState([]);
  const [summary, setSummary] = useState(null);
  const [calmStep, setCalmStep] = useState('menu');
  const [chat, setChat] = useState([
    { sender: 'bot', text: "Hi, I’m MindCare 🌱\nI’m here to help you check in with yourself gently and privately.\nHow are you feeling today?" }
  ]);
  const [history, setHistory] = useState([]); // For progress tracking
  const [reminderTime, setReminderTime] = useState('');
  const [reminderSet, setReminderSet] = useState(false);
  const [journalEntry, setJournalEntry] = useState('');
  const [journal, setJournal] = useState([]);
  const [showCustomCheckin, setShowCustomCheckin] = useState(false);
  const [customQuestions, setCustomQuestions] = useState([]);
  const [newCustomQuestion, setNewCustomQuestion] = useState('');
  const [customIdx, setCustomIdx] = useState(0);
  const [customAnswers, setCustomAnswers] = useState([]);
  const [customDone, setCustomDone] = useState(false);
  const [streak, setStreak] = useState(0);
  const [lastCheckinDate, setLastCheckinDate] = useState('');
  const [badges, setBadges] = useState([]);
  const [showSupportIntegration, setShowSupportIntegration] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const resources = [
    { name: 'National Suicide Prevention Lifeline (US)', url: 'https://988lifeline.org/', desc: '24/7 free and confidential support.' },
    { name: 'SAMHSA National Helpline', url: 'https://www.samhsa.gov/find-help/national-helpline', desc: 'Substance abuse and mental health help.' },
    { name: 'Crisis Text Line', url: 'https://www.crisistextline.org/', desc: 'Text HOME to 741741 for support.' },
    { name: 'Mental Health America', url: 'https://mhanational.org/', desc: 'Resources and support for mental health.' },
    { name: 'Find a Therapist', url: 'https://www.psychologytoday.com/us/therapists', desc: 'Directory to find mental health professionals.' },
    { name: 'International Helplines', url: 'https://www.opencounseling.com/suicide-hotlines', desc: 'Global crisis helplines.' }
  ];

  const startFeature = (feat) => {
    setFeature(feat);
    setStep(feat);
    setQuestionIdx(0);
    setAnswers([]);
    setResponse('');
    addMessage('Let’s begin!', 'bot');
  };

  const handleAnswer = async (idx) => {
    const newAnswers = [...answers, idx];
    let questions, endpoint, options;
    if (feature === 'stress') {
      questions = stressQuestions;
      endpoint = 'stress-check';
      options = likertOptions;
    } else if (feature === 'burnout') {
      questions = burnoutQuestions;
      endpoint = 'burnout-check';
      options = likertOptions;
    } else if (feature === 'sleep') {
      questions = sleepQuestions;
      endpoint = 'sleep-check';
      options = likertOptions;
    } else if (feature === 'anxiety') {
      questions = anxietyQuestions;
      endpoint = 'anxiety-check';
      options = likertOptions;
    } else if (feature === 'mood') {
      questions = moodQuestions;
      endpoint = 'mood-check';
      options = likertOptions;
    } else if (feature === 'risk') {
      questions = riskQuestions;
      endpoint = 'suicide-risk';
      options = riskOptions;
    } else if (feature === 'coping') {
      questions = copingQuestions;
      endpoint = 'coping-check';
      options = likertOptions;
    }
    if (questionIdx < questions.length - 1) {
      setAnswers(newAnswers);
      setQuestionIdx(questionIdx + 1);
    } else {
      // Submit to backend
      setLoading(true);
      try {
        const res = await fetch(`http://127.0.0.1:5000/api/${endpoint}`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ answers: newAnswers })
        });
        const data = await res.json();
        setResponse(data.result);
      } catch (err) {
        setResponse('Error connecting to backend.');
      }
      setLoading(false);
      setStep(feature + '-result');
      setAnswers([]);
      setQuestionIdx(0);
    }
  };

  const handleSummary = async () => {
    setLoading(true);
    try {
      const res = await fetch('http://127.0.0.1:5000/api/summary', {
        method: 'GET',
      });
      const data = await res.json();
      setSummary(data.summary);
      setStep('summary');
    } catch (err) {
      setSummary('Error connecting to backend.');
      setStep('summary');
    }
    setLoading(false);
  };

  const handleContinue = () => {
    setStep('main-menu');
    setResponse('');
    setFeature('');
  };

  const handleCalm = (activity) => {
    setCalmStep(activity);
    setStep('calm');
  };

  // Helper to add a message to chat
  const addMessage = (text, sender) => setChat((c) => [...c, { text, sender }]);

  // Mindfulness recommender logic (simple demo)
  const getMindfulnessRecommendation = () => {
    if (feature === 'stress' && response.includes('High')) return 'Try guided breathing or a body scan.';
    if (feature === 'burnout' && response.includes('Severe')) return 'Try gratitude journaling or a mindful walk.';
    if (feature === 'sleep' && response.includes('Disrupted')) return 'Try a calming music session before bed.';
    return 'Try a short breathing exercise or gratitude reflection.';
  };

  // Support recommender logic (simple demo)
  const getSupportRecommendation = () => {
    if (feature === 'risk' && response && response.toLowerCase().includes('risk')) {
      return 'You are not alone. Consider reaching out to a trusted person or a helpline.';
    }
    return 'If you ever need support, resources are available.';
  };

  // Mood tracker logic
  const handleMoodCheckin = (mood) => {
    setMoodHistory([...moodHistory, { date: new Date().toLocaleDateString(), mood }]);
    setChat((c) => [...c, { text: `Mood recorded: ${mood}`, sender: 'bot' }]);
    setShowMoodTracker(false);
  };

  // Save each result to history
  useEffect(() => {
    if (feature && step === feature + '-result' && response) {
      setHistory((prev) => [...prev, {
        date: new Date().toLocaleDateString(),
        feature,
        result: response
      }]);
    }
    // eslint-disable-next-line
  }, [step, response]);

  // Progress Tracking Chart Data
  const chartData = {
    labels: history.map((h, i) => `${h.feature} ${h.date}`),
    datasets: [
      {
        label: 'Results',
        data: history.map((h) => {
          if (h.result.includes('High') || h.result.includes('Severe')) return 3;
          if (h.result.includes('Moderate')) return 2;
          if (h.result.includes('Mild') || h.result.includes('Low') || h.result.includes('Good')) return 1;
          return 0;
        }),
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  // Reminder notification logic
  useEffect(() => {
    if (reminderSet && reminderTime) {
      const now = new Date();
      const [h, m] = reminderTime.split(":").map(Number);
      const reminderDate = new Date(now.getFullYear(), now.getMonth(), now.getDate(), h, m, 0, 0);
      let timeout = reminderDate - now;
      if (timeout < 0) timeout += 24 * 60 * 60 * 1000; // Next day
      const timer = setTimeout(() => {
        if (Notification.permission === 'granted') {
          new Notification('MindCare Reminder', { body: 'Time for your daily check-in!' });
        }
      }, timeout);
      return () => clearTimeout(timer);
    }
  }, [reminderSet, reminderTime]);

  // Request notification permission on mount
  useEffect(() => {
    if (window.Notification && Notification.permission !== 'granted') {
      Notification.requestPermission();
    }
  }, []);

  // Update streak and badges on check-in
  useEffect(() => {
    if (feature && step === feature + '-result' && response) {
      const today = new Date().toLocaleDateString();
      if (lastCheckinDate === today) return;
      if (lastCheckinDate) {
        const prev = new Date(lastCheckinDate);
        const curr = new Date(today);
        const diff = (curr - prev) / (1000 * 60 * 60 * 24);
        if (diff === 1) setStreak(s => s + 1);
        else setStreak(1);
      } else {
        setStreak(1);
      }
      setLastCheckinDate(today);
    }
    // eslint-disable-next-line
  }, [step, response]);

  useEffect(() => {
    if (streak === 3 && !badges.includes('3-Day Streak')) setBadges([...badges, '3-Day Streak']);
    if (streak === 7 && !badges.includes('7-Day Streak')) setBadges([...badges, '7-Day Streak']);
    if (streak === 30 && !badges.includes('30-Day Streak')) setBadges([...badges, '30-Day Streak']);
  }, [streak, badges]);

  useEffect(() => {
    document.body.className = darkMode ? 'dark' : '';
  }, [darkMode]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h2>MindCare Demo</h2>
        <div className="chat-area">
          {chat.map((msg, i) => <ChatBubble key={i} text={msg.text} sender={msg.sender} />)}
        </div>
        {/* Show Start button after welcome */}
        {step === 'welcome' && (
          <ButtonRow options={["Start"]} onClick={() => { setStep('main-menu'); addMessage('What would you like to explore?', 'bot'); }} />
        )}
        {/* Reminders & Notifications */}
        {step === 'reminder' && (
          <>
            <ChatBubble text={"Set a daily reminder time for your check-in (24h format):"} sender="bot" />
            <input
              type="time"
              value={reminderTime}
              onChange={e => setReminderTime(e.target.value)}
              style={{margin:8, padding:8, borderRadius:4, border:'1px solid #ccc'}}
            />
            <ButtonRow options={[reminderSet ? "Remove Reminder" : "Set Reminder", "Back to Main Menu"]}
              onClick={idx => {
                if (idx === 0) setReminderSet(!reminderSet);
                else { handleContinue(); addMessage('What would you like to explore?', 'bot'); }
              }}
            />
            {reminderSet && <ChatBubble text={`Reminder set for ${reminderTime} daily!`} sender="bot" />}
          </>
        )}
        {/* Resource Library */}
        {showResources && (
          <>
            <ChatBubble text={"Helpful Mental Health Resources:"} sender="bot" />
            <div className="bubble bot" style={{textAlign:'left'}}>
              <ul style={{margin:0, paddingLeft:20}}>
                {resources.map((r, i) => (
                  <li key={i} style={{marginBottom:8}}>
                    <a href={r.url} target="_blank" rel="noopener noreferrer">{r.name}</a><br/>
                    <span style={{fontSize:'0.95em', color:'#555'}}>{r.desc}</span>
                  </li>
                ))}
              </ul>
            </div>
            <ButtonRow options={["Back to Main Menu"]} onClick={() => setShowResources(false)} />
          </>
        )}
        {/* Mood Journal */}
        {showJournal && (
          <>
            <ChatBubble text={"Write about your feelings today. Your journal is private and stays on this device."} sender="bot" />
            <textarea
              value={journalEntry}
              onChange={e => setJournalEntry(e.target.value)}
              rows={4}
              style={{width:'90%', margin:8, padding:8, borderRadius:4, border:'1px solid #ccc'}}
              placeholder="Type your thoughts here..."
            />
            <ButtonRow options={["Save Entry", "Back to Main Menu"]}
              onClick={idx => {
                if (idx === 0 && journalEntry.trim()) {
                  setJournal([...journal, { date: new Date().toLocaleDateString(), text: journalEntry }]);
                  setJournalEntry('');
                } else if (idx === 1) {
                  setShowJournal(false);
                  setJournalEntry('');
                }
              }}
            />
            {journal.length > 0 && (
              <div className="bubble bot" style={{textAlign:'left', maxHeight:150, overflowY:'auto'}}>
                <b>Your Journal Entries:</b>
                <ul style={{margin:0, paddingLeft:20}}>
                  {journal.map((j, i) => (
                    <li key={i} style={{marginBottom:8}}>
                      <b>{j.date}:</b> {j.text}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </>
        )}
        {/* Customizable Check-ins */}
        {showCustomCheckin && (
          <>
            {!customQuestions.length && !customDone && (
              <>
                <ChatBubble text={"Add your own check-in questions (one at a time):"} sender="bot" />
                <input
                  type="text"
                  value={newCustomQuestion}
                  onChange={e => setNewCustomQuestion(e.target.value)}
                  style={{margin:8, padding:8, borderRadius:4, border:'1px solid #ccc', width:'90%'}}
                  placeholder="Type your question here..."
                />
                <ButtonRow options={["Add Question", "Start Check-in", "Back to Main Menu"]}
                  onClick={idx => {
                    if (idx === 0 && newCustomQuestion.trim()) {
                      setCustomQuestions([...customQuestions, newCustomQuestion]);
                      setNewCustomQuestion('');
                    } else if (idx === 1 && customQuestions.length) {
                      setCustomIdx(0);
                    } else if (idx === 2) {
                      setShowCustomCheckin(false);
                      setCustomQuestions([]);
                      setNewCustomQuestion('');
                      setCustomIdx(0);
                      setCustomAnswers([]);
                      setCustomDone(false);
                    }
                  }}
                />
              </>
            )}
            {customQuestions.length > 0 && customIdx < customQuestions.length && !customDone && (
              <>
                <ChatBubble text={customQuestions[customIdx]} sender="bot" />
                <ButtonRow options={likertOptions}
                  onClick={idx => {
                    setCustomAnswers([...customAnswers, idx]);
                    if (customIdx < customQuestions.length - 1) {
                      setCustomIdx(customIdx + 1);
                    } else {
                      setCustomDone(true);
                    }
                  }}
                />
              </>
            )}
            {customDone && (
              <>
                <ChatBubble text={"Check-in complete! Here are your answers:"} sender="bot" />
                <div className="bubble bot" style={{textAlign:'left'}}>
                  <ul style={{margin:0, paddingLeft:20}}>
                    {customQuestions.map((q, i) => (
                      <li key={i}>{q} <b>-</b> {likertOptions[customAnswers[i]]}</li>
                    ))}
                  </ul>
                </div>
                <ButtonRow options={["Back to Main Menu"]} onClick={() => {
                  setShowCustomCheckin(false);
                  setCustomQuestions([]);
                  setNewCustomQuestion('');
                  setCustomIdx(0);
                  setCustomAnswers([]);
                  setCustomDone(false);
                }} />
              </>
            )}
          </>
        )}
        {/* Professional Support Integration (Coming Soon) */}
        {showSupportIntegration && (
          <>
            <ChatBubble text={"Professional Support Integration"} sender="bot" />
            <div className="bubble bot" style={{textAlign:'left'}}>
              <i>This feature will allow you to connect with a counselor or schedule a session if high risk is detected.<br/>We will add this feature soon!</i>
            </div>
            <ButtonRow options={["Back to Main Menu"]} onClick={() => setShowSupportIntegration(false)} />
          </>
        )}
        {/* Updated main menu */}
        {step === 'main-menu' && !showMindfulness && !showSupport && !showMoodTracker && !showResources && !showJournal && !showCustomCheckin && !showSupportIntegration && (
          <ButtonRow options={[
            'Stress Self-Test Tool',
            'Burnout Risk Calculator',
            'Personalized Mindfulness Recommender',
            'Mental Health Support Recommender',
            'Daily Check-in / Mood Tracker',
            '📊 My Check-In Summary',
            '🧘 Calm My Mind',
            '📈 Progress Tracking',
            '⏰ Set Daily Reminder',
            '📚 Resource Library',
            '📔 Mood Journal',
            '➕ Custom Check-in',
            '👩‍⚕️ Professional Support']}
            onClick={(idx) => {
              if (idx === 0) { startFeature('stress'); addMessage('Let’s begin the Stress Self-Test.', 'bot'); }
              else if (idx === 1) { startFeature('burnout'); addMessage('Let’s begin the Burnout Risk Calculator.', 'bot'); }
              else if (idx === 2) setShowMindfulness(true);
              else if (idx === 3) setShowSupport(true);
              else if (idx === 4) setShowMoodTracker(true);
              else if (idx === 5) handleSummary();
              else if (idx === 6) { setCalmStep('menu'); setStep('calm'); addMessage('Choose a calming activity to try right now:', 'bot'); }
              else if (idx === 7) setStep('progress');
              else if (idx === 8) setStep('reminder');
              else if (idx === 9) setShowResources(true);
              else if (idx === 10) setShowJournal(true);
              else if (idx === 11) setShowCustomCheckin(true);
              else if (idx === 12) setShowSupportIntegration(true);
            }}
          />
        )}
        {/* Mindfulness Recommender */}
        {showMindfulness && (
          <>
            <ChatBubble text={getMindfulnessRecommendation()} sender="bot" />
            <ButtonRow options={["Back to Main Menu"]} onClick={() => setShowMindfulness(false)} />
          </>
        )}
        {/* Support Recommender */}
        {showSupport && (
          <>
            <ChatBubble text={getSupportRecommendation()} sender="bot" />
            <ButtonRow options={["Back to Main Menu"]} onClick={() => setShowSupport(false)} />
          </>
        )}
        {/* Mood Tracker */}
        {showMoodTracker && (
          <>
            <ChatBubble text={"How are you feeling today?"} sender="bot" />
            <ButtonRow options={["😊 Great", "🙂 Okay", "😟 Not so good", "😞 Overwhelmed"]} onClick={(idx) => {
              const moods = ["Great", "Okay", "Not so good", "Overwhelmed"];
              handleMoodCheckin(moods[idx]);
            }} />
            {moodHistory.length > 0 && (
              <div className="bubble bot"><pre style={{textAlign:'left', margin:0}}>{moodHistory.map(m => `${m.date}: ${m.mood}`).join('\n')}</pre></div>
            )}
            <ButtonRow options={["Back to Main Menu"]} onClick={() => setShowMoodTracker(false)} />
          </>
        )}
        {/* For each feature, show question as bot bubble and options as user buttons */}
        {['stress','burnout','sleep','anxiety','mood','risk','coping'].includes(feature) && step === feature && (
          <>
            <ChatBubble text={
              feature === 'stress' ? stressQuestions[questionIdx] :
              feature === 'burnout' ? burnoutQuestions[questionIdx] :
              feature === 'sleep' ? sleepQuestions[questionIdx] :
              feature === 'anxiety' ? anxietyQuestions[questionIdx] :
              feature === 'mood' ? moodQuestions[questionIdx] :
              feature === 'risk' ? riskQuestions[questionIdx] :
              copingQuestions[questionIdx]
            } sender="bot" />
            <ButtonRow
              options={feature === 'risk' ? riskOptions : likertOptions}
              onClick={async (idx) => {
                addMessage((feature === 'risk' ? riskOptions : likertOptions)[idx], 'user');
                await handleAnswer(idx);
              }}
              disabled={loading}
            />
          </>
        )}
        {/* Show result as bot bubble and continue button */}
        {feature && step === feature + '-result' && (
          <>
            <ChatBubble text={response} sender="bot" />
            {/* Personalized Recommendation */}
            {feature === 'stress' && response && (
              <ChatBubble text={
                response.includes('High') ?
                  'Recommendation: Try guided breathing, take a short walk, or talk to a friend.' :
                response.includes('Moderate') ?
                  'Recommendation: Practice mindfulness or journaling to manage stress.' :
                  'Recommendation: Keep up your healthy habits!'
              } sender="bot" />
            )}
            {feature === 'burnout' && response && (
              <ChatBubble text={
                response.includes('Severe') ?
                  'Recommendation: Take a break, reach out for support, and consider professional help.' :
                response.includes('Moderate') ?
                  'Recommendation: Set boundaries and make time for self-care.' :
                  'Recommendation: Maintain your work-life balance!'
              } sender="bot" />
            )}
            <ButtonRow options={["Back to Main Menu"]} onClick={() => { handleContinue(); addMessage('What would you like to explore?', 'bot'); }} />
          </>
        )}
        {/* Calm My Mind feature as chat bubbles */}
        {step === 'calm' && (
          <>
            {calmStep === 'menu' && (
              <ButtonRow options={[
                '🌬️ Guided Breathing (1 min)',
                '✍️ Gratitude Prompt',
                '🎵 Calming Music',
                '🧘 Body Scan (2 min)',
                '❌ Exit']}
                onClick={(idx) => {
                  const acts = ['breathing','gratitude','music','bodyscan','exit'];
                  if (acts[idx] === 'exit') { handleContinue(); addMessage('What would you like to explore?', 'bot'); }
                  else setCalmStep(acts[idx]);
                }}
              />
            )}
            {calmStep === 'breathing' && (
              <>
                <ChatBubble text={"Let’s do a simple breathing exercise.\nInhale... 1... 2... 3... 4...\nExhale... 1... 2... 3... 4...\nRepeat for one minute, focusing on your breath."} sender="bot" />
                <ButtonRow options={["Back"]} onClick={() => setCalmStep('menu')} />
              </>
            )}
            {calmStep === 'gratitude' && (
              <>
                <ChatBubble text={"Take a moment to think of one thing you’re grateful for today.\nWrite it down or just reflect on it quietly."} sender="bot" />
                <ButtonRow options={["Back"]} onClick={() => setCalmStep('menu')} />
              </>
            )}
            {calmStep === 'music' && (
              <>
                <ChatBubble text={"Play some calming music. Here’s a suggestion:"} sender="bot" />
                <a href="https://www.youtube.com/watch?v=2OEL4P1Rz04" target="_blank" rel="noopener noreferrer">Listen to relaxing music</a>
                <ButtonRow options={["Back"]} onClick={() => setCalmStep('menu')} />
              </>
            )}
            {calmStep === 'bodyscan' && (
              <>
                <ChatBubble text={"Close your eyes and slowly scan your body from head to toe, noticing any tension and letting it go.\nTake 2 minutes for this mindful check-in."} sender="bot" />
                <ButtonRow options={["Back"]} onClick={() => setCalmStep('menu')} />
              </>
            )}
          </>
        )}
        {/* Summary as chat bubble */}
        {step === 'summary' && (
          <>
            <ChatBubble text={"Here’s a quick overview of your recent responses:"} sender="bot" />
            <div className="bubble bot"><pre style={{textAlign:'left', margin:0}}>{summary}</pre></div>
            <ButtonRow options={["Back to Main Menu"]} onClick={() => { handleContinue(); addMessage('What would you like to explore?', 'bot'); }} />
          </>
        )}
        {/* Progress Tracking Chart */}
        {step === 'progress' && (
          <>
            <ChatBubble text={"Your Check-In Progress Over Time:"} sender="bot" />
            <div style={{background:'#fff', borderRadius:8, padding:16, margin:16}}>
              <Line data={chartData} />
            </div>
            <ButtonRow options={["Back to Main Menu"]} onClick={() => { handleContinue(); addMessage('What would you like to explore?', 'bot'); }} />
          </>
        )}
        {/* Gamification: Streaks and Badges */}
        <div style={{margin:'16px 0'}}>
          <span role="img" aria-label="fire">🔥</span> <b>Streak:</b> {streak} day{streak !== 1 ? 's' : ''}
          {badges.length > 0 && (
            <div style={{marginTop:8}}>
              <b>Badges:</b> {badges.map(b => <span key={b} style={{marginRight:8, background:'#ffe066', borderRadius:4, padding:'2px 8px'}}>{b}</span>)}
            </div>
          )}
        </div>
        {/* Dark Mode Toggle */}
        <div style={{position:'absolute', top:16, right:24, zIndex:10}}>
          <button
            aria-label={darkMode ? 'Switch to light mode' : 'Switch to dark mode'}
            onClick={() => setDarkMode(d => !d)}
            style={{background:darkMode?'#222':'#fff', color:darkMode?'#fff':'#222', border:'1px solid #888', borderRadius:20, padding:'6px 16px', fontSize:'1rem', cursor:'pointer'}}
          >
            {darkMode ? '☀️ Light' : '🌙 Dark'}
          </button>
        </div>
      </header>
    </div>
  );
}

export default App;
