import React, { useState } from 'react';
import './SadOMeterPage.css'; // Reuse styles for consistency

const practices = [
  {
    title: '4-7-8 Guided Breathing',
    desc: 'Breathe in for 4 seconds, hold for 7, exhale for 8. Repeat 4 times.',
    emoji: 'üå¨Ô∏è',
    link: 'https://www.youtube.com/watch?v=YRPh_GaiL8s',
    type: 'video'
  },
  {
    title: '5-Minute Meditation',
    desc: 'A short guided meditation to reset your mind.',
    emoji: 'üßò',
    link: 'https://www.youtube.com/watch?v=inpok4MKVLM',
    type: 'video'
  },
  {
    title: 'Drop Shoulders Visual',
    desc: 'Relax your shoulders. Imagine them melting down. Try it now!',
    emoji: 'ü´≥',
    type: 'visual'
  },
  {
    title: 'Mind-Dumping Journal Prompt',
    desc: 'Write down everything on your mind for 2 minutes. No filter.',
    emoji: 'üìù',
    type: 'prompt'
  },
  {
    title: 'Nature Walk',
    desc: 'Step outside for 5 minutes. Notice the sights, sounds, and smells.',
    emoji: 'üå≥',
    type: 'activity'
  },
  {
    title: 'Herbal Tea Ritual',
    desc: 'Make yourself a cup of herbal tea. Sip slowly and mindfully.',
    emoji: 'üçµ',
    type: 'activity'
  },
  {
    title: 'Progressive Muscle Relaxation',
    desc: 'Tense and relax each muscle group from head to toe.',
    emoji: 'üí™',
    type: 'activity'
  },
  {
    title: 'Coloring Exercise',
    desc: 'Download a coloring page and color for 5 minutes.',
    emoji: 'üé®',
    link: 'https://www.crayola.com/free-coloring-pages/',
    type: 'download'
  },
  {
    title: 'Digital Detox',
    desc: 'Put your phone away for 30 minutes. The world can wait.',
    emoji: 'üìµ',
    type: 'activity'
  }
];

function ZenCard({ practice }) {
  return (
    <div className="sadometer-question-card" style={{marginBottom: 18}}>
      <div style={{fontSize: 32, marginBottom: 8}}>{practice.emoji}</div>
      <div className="sadometer-question" style={{fontWeight: 700}}>{practice.title}</div>
      <div style={{margin: '10px 0 8px 0'}}>{practice.desc}</div>
      {practice.link && (
        <a href={practice.link} target="_blank" rel="noopener noreferrer" className="feature-btn" style={{marginTop: 8, display: 'inline-block'}}>
          {practice.type === 'video' ? 'Watch Video' : practice.type === 'download' ? 'Download Template' : 'Learn More'}
        </a>
      )}
    </div>
  );
}

export default function ZenRecommenderPage() {
  const [mood, setMood] = useState(null);
  const [step, setStep] = useState(0);
  const [showBreak, setShowBreak] = useState(false);

  // Mood-driven activity sets
  const moodSets = {
    'I want to calm down': [
      practices[0], // 4-7-8 Breathing
      practices[1], // Meditation
      practices[2], // Drop Shoulders
    ],
    'I want motivation': [
      { ...practices[1], desc: 'Try this 5-minute motivation audio to get pumped!' },
      { ...practices[3], desc: 'Write down one thing you want to achieve today.' },
      { ...practices[5], desc: 'Make a cup of tea and plan your next win.' },
    ],
    'I want to unplug': [
      practices[8], // Digital Detox
      practices[6], // Progressive Muscle Relaxation
      practices[7], // Coloring
    ],
    'I want peace and quiet': [
      practices[4], // Nature Walk
      practices[2], // Drop Shoulders
      practices[3], // Mind-Dumping
    ],
  };

  const activities = mood ? moodSets[mood] : [];

  const handleMood = (m) => {
    setMood(m);
    setStep(0);
  };
  const handleNext = () => {
    if ((step + 1) % 3 === 0 && step !== activities.length - 1) {
      setShowBreak(true);
    } else {
      setStep(s => s + 1);
    }
  };
  const handleContinue = () => {
    setShowBreak(false);
    setStep(s => s + 1);
  };

  return (
    <div className="sadometer-root">
      <h2 style={{marginBottom: 0}}>Zen Recommender <span style={{fontSize: 28}}>üßò</span></h2>
      <div className="sadometer-progress-bar">
        <div className="sadometer-progress" style={{width: `${(step / (activities.length || 1)) * 100}%`}} />
      </div>
      {!mood && (
        <div className="sadometer-question-card">
          <div className="sadometer-question" style={{fontWeight: 700, fontSize: 20}}>
            How do you want to feel today?
          </div>
          <div className="sadometer-options">
            {Object.keys(moodSets).map((m, i) => (
              <button
                key={i}
                className="sadometer-option-btn"
                onClick={() => handleMood(m)}
                style={{fontSize: 17, margin: 6}}
              >
                {m}
              </button>
            ))}
          </div>
        </div>
      )}
      {mood && step < activities.length && !showBreak && (
        <>
          <ZenCard practice={activities[step]} />
          <button className="feature-btn" onClick={handleNext} style={{marginTop: 8}}>
            Next Practice
          </button>
        </>
      )}
      {showBreak && (
        <div className="sadometer-break-card">
          <div style={{fontSize: 32, marginBottom: 12}}>üßò‚Äç‚ôÇÔ∏è Need a break?</div>
          <div style={{marginBottom: 16}}>Take a deep breath, stretch, or grab some water. Ready to continue?</div>
          <button className="sadometer-option-btn" onClick={handleContinue}>I‚Äôm Ready!</button>
        </div>
      )}
      {mood && step === activities.length && (
        <div style={{marginTop: 32, fontWeight: 700, fontSize: 20, color: '#4caf50'}}>
          You‚Äôve completed your Zen session! Come back anytime for more calm. üå±
        </div>
      )}
      <a href="/" className="feature-btn" style={{marginTop: 32, display: 'inline-block'}}>Back to Home</a>
      {/* Floating break prompt */}
      {mood && step < activities.length && !showBreak && (
        <div className="sadometer-floating-break" onClick={() => setShowBreak(true)}>
          <span role="img" aria-label="break">üßò‚Äç‚ôÄÔ∏è</span> Need a break?
        </div>
      )}
    </div>
  );
}
